INSERT INTO `ht_article` (`id`, `title`, `cat_id`, `content`, `public_date`, `hits`, `list_order`) VALUES
(1, '董事长致辞', 17, '<p><span style="font-family: Arial, Helvetica, sans-serif, 新宋体; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);">网站建设中....</span></p>', '1503849600', 1, 2),
(7, '知识产权', 17, '<p><span style="color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);">公司研制的《汉腾集成协作平台及其系列子系统》，拥有业界领先的自主核心技术和完全知识产权, 部分已申请软件著作权，部分在申请中。</span></p>', '1503849600', 0, 3),
(6, '关于汉腾', 17, '<p style="text-indent: 2em;"><span style="background-color: rgb(255, 255, 255); color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; text-indent: 28px;">合肥汉腾信息技术有限公司(简称汉腾信息)成立于2011年9月。由软件技术、智能信息处理技术和云计算等方面的资深专家联合组建。团队核心成员均来自海内外名校。目前主要致力于新一代企业集成协作平台及相关软件产品的研究与开发。公司研制的《汉腾集成协作平台及其系列子系统》，拥有业界领先的自主核心技术和完全知识产权。汉腾信息提供了一个全面的企业级信息管理平台，帮助企业用户打造一个集成、协作、共享的企业数字化管理和运营系统，降低管理费用，优化企业运营和提高生产力，实现智慧企业。帮助客户实现更大价值，取得更多成功，是我们创新领先技术的动力源泉。</span></p>', '1503676800', 1, 1),
(8, '使命愿景和价值观', 17, '<p><span style="font-family: Arial, Helvetica, sans-serif, 新宋体; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);">网站建设中....</span></p>', '1503849600', 0, 4),
(16, '企业信息化的重要性与迫切性', 5, '<p style="text-indent: 2em;" dir="ltr">企业信息化是指企业充分运用信息技术，对企业内部信息资源进行组织、开发和利用，不断提高管理、决策、生产、经营、服务的效率和水平。通过改造企业生产过程、管理方式和营销途径等业务流程，以业务流程优化或重组为基础，基于软件技术、智能信息处理技术和网络技术，达到企业管理生产经营活动中的所有信息集成化、协作化，实现企业内外部信息的共享和有效利用，以降低管和运营成本，提高企业的经济效益和市场竞争力。企业信息化涉及到企业管理理念的创新，管理流程的优化，管理团队的重组和管理手段的创新。</p><p style="text-indent: 2em;" dir="ltr">企业的信息化建设消除了企业与市场的空间距离，拓展了国内国际市场，缩短了企业产品进入消费者手中的时间，有效 弥补中小企业的天生不足，强化了中小企业专业化经营的特长；同时提高其市场竞争力，实现精益化管理，加强与上下游企业之间的协同，提升企业的核心竞争力。在知识经济的新时代，企业面临着日趋激烈的市场竞争，企业应早日推进企业信息化建设来增强自身实力和竞争力，企业才能更加充满活力，从而使企业获得持续的发展。</p><p style="text-indent: 2em;" dir="ltr">企业信息化是我国信息化工作的核心和基础，也是现阶段国家信息化工作的重点和难点。借助IT技术加快企业技术创新，管理创新、制度创新，是实现“以信息化带动工业化”战略的切入点。企业信息化建设帮助企业提高管理水平，促进管理现代化、转换经营机制、建立现代企业制度、有效降低成本、加快技术进步、增强企业市场竞争力、提高经济效益，是企业推动各项工作创新和升级的突破口，也是解决当前企业管理中突出问题的有效措施。从微观来看，企业信息化可以改善员工的工作环境，降低劳动强度，用计算机实现繁杂、重复的简单重复性体力劳动。</p><p dir="ltr"><br/></p>', '1504022400', 0, 0),
(17, '企业信息化面临的挑战与棘手问题', 5, '<p style="text-indent: 0em;">1、信息膨胀</p><p style="text-indent: 0em;">随着各种IT系统的应用，大多数企业需要处理的信息正在迅速膨胀，据统计，平均每12-18个月就会扩展一倍。</p><p style="text-indent: 0em;">&nbsp;</p><p style="text-indent: 0em;">2、信息孤岛</p><p style="text-indent: 0em;">企业各种相互独立的应用系统如OA、ERP、CRM、HR、BI以及财务软件等，给企业的融合管理设置了障碍、缺乏一个统一的界面、应用和数据无法交换共享。</p><p style="text-indent: 0em;">&nbsp;</p><p style="text-indent: 0em;">3、信息碎片</p><p style="text-indent: 0em;">90%信息非结构化。对于企业管理者来说是个信息死角；</p><p style="text-indent: 0em;">企业员工、客户、供应商和合作伙伴无法获得个性化信息；</p><p style="text-indent: 0em;">&nbsp;</p><p style="text-indent: 0em;">4、投资成本压力</p><p style="text-indent: 0em;">企业面临IT投资与开销以及如何最大限度提高投资回报的压力</p><p><br/></p>', '1504022400', 0, 0),
(18, '汉腾企业集成协作平台解决方案简介', 6, '<p style="text-indent: 2em;">汉腾企业集成协作平台解决方案, 简单的说就是一个应用框架，将企业的所有应用与信息集成到一个统一的信息管理平台上，是一个基于Web的应用系统，使企业员工、客户、供应商和合作伙伴能从单一渠道访问所需信息，是一个用来构建一个门户站点的工具, 一个开发企业协作平台的解决方案。</p><p style="text-indent: 2em;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 汉腾企业协同平台将企业的所有应用系统、数据资源统一集成到一个信息管理平台之上，提供简单、熟悉、统一的一体化界面. 它利用了单点登录和企业应用程序集成功能，以及灵活的部署选项和管理工具，将来自不同系统的信息集成到一个解决方案中, 消除信息孤岛，帮助企业开发出智能的门户站点。它通过灵活的部署选项和管理工具，将多种系统中的信息整合到一个解决方案中。这些集成特性使您可以控制公司资源的使用。各站点能够无缝连接到用户、团队和知识，使得端到端的合作更为容易，实现个人、团队和各类信息资源紧密链接和高效协作, 并根据每个用户角色和使用特点的不同，形成个性化的应用界面。公司企业可以根据受众的公司职位、团队身份、兴趣或其他可以设置的成员规则来设定信息、程序和更新。用户可以通过门户内容和布局的定制和个性化以及目标受众，更快地找到相关信息。</p><p style="text-indent: 2em;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 汉腾企业协同平台使得公司、公司各部门以及用户可以定制和个性化门户站点。相关内容或业务可以通过Web子系统发送到门户站点，例如即时通讯、新闻、销售和其他公司数据。另外，IT部门可以锁定特殊的子系统或页面区域，这样公司就能够通过门户站点向所有的员工发布重要的信息。每个用户拥都可以拥有自己的个人门户站点，该站点被称为“我的站点”。在这个站点上，他们可以组织日常使用的信息、程序和站点页面。</p><p style="text-indent: 2em;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 汉腾企业协同平台为公司各部门、团队协作、公司员工、公司客户、供货商及合作伙伴提供了站点，并且实现了大量智能空间。将这些空间与个人、知识和业务流程连接起来，可以实现智慧的企业。</p><br style="padding: 0px; margin: 0px;"/><p style="text-indent: 2em;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 汉腾企业协同平台可以使授权用户为个人、信息和公司创建门户站点、管理各门户站点；在各站点发布和管理信息；汉腾企业协同系统提供了众多可选用的Web子系统组件，用户可以从子系统组件库中进行选择，在公司、部门或个人的门户站点上根据需要添加或删除Web子系统组件。所有这些操作和管理不需要任何Web开发经验，可以由非IT技术人员来完成。公司企业IT部门也可以借助汉腾企业协同平台技术，无须任何额外的技术学习，可以直接开发自己的Web子系统组件并轻松无缝集成到汉腾企业协同平台系统。公司IT部门可以根据需要，直接利用汉腾企业协同平台的提供的接口，轻松开发各种应用程序系统，无缝集成到协同平台，这种集成开发将大大减少公司企业新系统开发、培训和维护的时间和费用。</p><br style="padding: 0px; margin: 0px;"/><p style="text-indent: 2em;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;汉腾企业协同平台门户站点构建在一个可伸缩的、高度分散的体系结构上，使得公司门户站点能够随着公司需求的增长而扩展。汉腾企业协同平台可根据不同行业的管理和业务特点提供相应的、具有灵活部署和管理工具的协作解决方案。 它还将实现更高层次的价值，提供一个可访问的、友好部署的、高度伸缩的平台，用于开发扩展的应用程序系统，建立一个全面协同的企业数字化管理和运营系统，降低管理费用、优化企业运营和提高生产力，实现智慧企业，为您的企业插上腾飞的双翼。</p><p><br/></p>', '1504022400', 0, 0),
(19, '汉腾企业集成协作平台解决方案的优势', 6, '<p style="text-indent: 0em;">1&nbsp;统一信息门户</p><p style="text-indent: 2em;">构建协同合作平台，实现公司、部门、员工、以及外部供应商之间的互动沟通与协作。公司一般行政人员（非IT专业人员）获权后即可轻松创建门户站点，任意添加各种网页模块，实现布局、模板的修改，发布网站内容，部署配置各功能性子系统，建立企业内各种信息资源采集、展示平台，为企业员工、供货商及合作伙伴建立协作、知识与经验共享的通道。</p><p style="text-indent: 0em;">&nbsp;</p><p style="text-indent: 0em;"><span style="text-indent: 2em;">2 &nbsp;统一沟通平台</span></p><p style="text-indent: 2em;">系统提供群创建与管理，提供群内协作与知识共享。为本地及远程用户提供即时消息、音频视频会议、多人会议等功能。提供邮件中心，集成内外网电子邮件，日历日程计划的安排与共享， 工作计划的安排与任务分配及完成进展情况的掌控。</p><p style="text-indent: 2em;"><span style="text-indent: 0em;"><br/></span></p><p style="text-indent: 0em;"><span style="text-indent: 0em;">3&nbsp;安全可靠的角色管理与权限控制</span></p><p style="text-indent: 2em;">可以为各员工以及外部客户创建不同的角色，根据角色和安全级别的不同为其对平台资源的使用赋予不同的权限。可以定制出个性化的应用门户，每个人都可以看到不同的信息或平台提供的为不同用户定制的信息。支持搜索结果安全过滤，用户在搜索结果中不会看到没有权限的文件。</p><p style="text-indent: 0em;">&nbsp;</p><p style="text-indent: 0em;">4 节省用户开发成本，降低IT成本和复杂性</p><p style="text-indent: 2em;">作为一体化平台，管理整个企业内的 内网、 外网 和 Internet 应用程序。它构建于可伸缩的开放式体系结构之上，可以轻松集成第三方应用程序和后端系统。可以二次定制开发新应用系统，用户做二次开发时，无须再做基础架构开发，借助协同平台架构技术，用户可以轻松开发新系统并无缝集成，极大的节省用户IT部门二次开发的研发费用与时间。</p><p style="text-indent: 0em;"><br/></p><p style="text-indent: 0em;">5&nbsp;强大的文档管理与协作<br/></p><p style="text-indent: 2em;">文档权限控制、信息权限管理、审批工作流等功能为您搭建合理的企业办公平台。实现文档的集中存储，可以轻松查找、共享和使用其中的信息而不泄漏敏感信息。建立销售、技术、咨询等各类知识库，为各部门高效协同合作提供基础。</p><p style="text-indent: 0em;">&nbsp;</p><p style="text-indent: 0em;">6&nbsp;通过简化日常的业务活动来提高员工生产力</p><p style="text-indent: 2em;">实施协作工作区、自助 Web 发布、业务流程工作流和智能表单，提高员工生产力。无须编写任何代码即可轻松创建自由工作流程，也可以利用现成的工作流模板来启动、跟踪和报告常见的业务活动，例如，审阅和批准文档、跟踪问题以及收集签名。可以灵活修改和扩展这些现成的工作流程。</p><p style="text-indent: 0em;">&nbsp;</p><p style="text-indent: 0em;">7&nbsp;实现企业决策支持：</p><p style="text-indent: 2em;">整合商业智能（BI）工具对数据进行深度挖掘，提升商业洞察力，及时反馈数据于公司决策者共享。</p><p style="text-indent: 0em;">&nbsp;</p><p style="text-indent: 0em;">8&nbsp;提供简单、熟悉、一致的用户体验，简化管理和培训</p><p style="text-indent: 2em;">无论是为工作组、企业还是 Internet 上的客户提供服务，汉腾企业协同平台站点均构建在同一基础结构上。IT 专业人员可以集中管理和监视站点、集成应用程序以及通过 Web 子系统和模板重复使用流行的解决方案；轻松管理平台系统中的各子系统组件。为最终用户和 IT 管理及监管提供一个易于使用的界面，可以提升价值，同时减少成本、时间和工作量。</p><p><br/></p>', '1504022400', 0, 0),
(20, '视频会议子系统组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">1、完全BS架构，保证了音视频通话，由于浏览器的原生态支持，避免了繁琐的插件安装。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="/ueditor/php/upload/image/20170830/1504053252621981.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">2、P2P通信方式，彻底解放了服务器。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">3、内网穿透，即使不在统一局域网也可建立会话。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">4、一对多或多对多的聊天方式，用户随心所欲。</span></p><p><br/></p>', '1504022400', 0, 0),
(21, '日程管理子系统组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">1、日程可以按日、按周、按月显示。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_14_18_24.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_14_17_15.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_14_09_51.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">2、点击日程显示详细信息。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_14_10_47.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">3、点击空白处快速添加日程安排。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_14_11_34.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">4、订阅、管理日程。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_14_12_59.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">5、分享日程。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_14_13_56.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p><br/></p>', '1504022400', 0, 0),
(22, '投票子系统组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">1、可以动态添加投票选项。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">2、可以设置单选、多选及限制选中的个数，可以设置是否公开，可以设置截止日期。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_13_28_59.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">3、图形化显示投票结果。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_13_33_22.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">4、用户可以对投票进行点赞及评论。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_13_35_23.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">5、将用户可投票及已投票主题进行分开显示。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_13_36_42.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p><br/></p>', '1504022400', 0, 0),
(23, '教务管理子系统组件', 7, '<p><span style="padding: 0px; margin: 0px; font-size: 14px;">1、采用树形结构，对学校基础信息维护，直观明了。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_11_21_19.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">2、强大的排课引擎，采用基因遗传算法，对课程、教师、教室、时间四个基元进行完美的组合。</span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_11_43_18.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">3、多种方式的选课，可以以班级为单位进行预选课程。单个学生也可以按照需要，自由灵活地选课。</span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_11_23_52.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">4、多种课表的生成：学生课表、教师课表、教室课表以及班级课表。</span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><span style="padding: 0px; margin: 0px; color: rgb(0, 128, 0);">教师课表：</span></span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_11_25_03.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><span style="padding: 0px; margin: 0px; color: rgb(0, 128, 0);">学生课表：</span></span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_11_27_35.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><span style="padding: 0px; margin: 0px; color: rgb(0, 128, 0);">教室课表：</span></span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_11_28_26.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">5、课表的打印功能。</span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_11_34_06.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">6、教师的成绩录入功能，录入成绩前，先设定好各项成绩系统，录入时系统将自动计算总成绩，大大简化了教师的成绩录入。</span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_11_36_01.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p><br/></p>', '1504022400', 0, 0),
(24, '公文管理子系统组件', 7, '<p><span style="padding: 0px; margin: 0px; font-size: 14px;">1、系统预定义好常用公文模板，便捷快速发送公文。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_10_07_12.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">2、发送公文可以使用公文模板，并添加多个本地附件。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_10_04_48.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto; font-size: 16px;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">3、用户对收到的公文进行签阅，转发。并可对公文附件中的Word文档进行签章。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_09_57_51.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_09_58_14.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">4、用户可以对已发送公文进行锁定，接收人将不可再对公文进行转发或审批。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_10_02_17.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">5、用户可以通过短信息或者Email催办对已发公文进行，提升公文办理效率。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_09_49_04.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">6、发文管理，相关人员将发至外单位的公文进行拟稿，领导审批后进行归档。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_09_41_47.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto; font-size: 16px;"/></span></p><p>&nbsp;</p><p><span style="padding: 0px; margin: 0px; font-size: 14px;">7、收文管理，相关人员将外单位发送来的公文进行拟稿，领导审批后进行归档。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_09_43_50.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p><br/></p>', '1504022400', 0, 0),
(25, '报表管理子系统组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">1、系统预定义好报表模板，便捷快速填写报表。&nbsp;<img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_17_46_10.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">2、报表添加类别，方便管理。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_17_47_22.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">3、灵活的权限控制，具备权限的角色才可以进行相应操作。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">4、报表填写，可以使用报表模板在线填写，亦可上传本地报表文件。&nbsp;<img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_17_54_54.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_17_54_40.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">5、报表在线签章，防止文件被篡改。&nbsp;<img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_18_00_21.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">6、报表浏览功能，方便企业、机构领导对报表进行查阅。<img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_18_01_28.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p><br/></p>', '1504022400', 0, 0),
(26, '工作流程管理子系统组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">1、 定义流程模板，快速便捷发起流程。</span><img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_15_10_25.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">2、自定义流程，无限拓展流程应用。</span><br style="padding: 0px; margin: 0px;"/><img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_16_21_25.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">3、流程带有附件功能，满足用户各种需求。</span><img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_15_12_06.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">4、内嵌Office控件，对Word文档进行签字盖章，有效防止文件篡改。</span><img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_15_13_09.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_15_13_38.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">5、自定义表单功能，将必要信息直接显示于页面，一目了然，免去了使用Excel作为附件时的各种繁琐操作。</span><img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_16_21_58.png" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">6、即时的短信及邮件提醒功能，极大提升办公效率。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">7、炫酷的流程动态图形展示功能，流程状态结果一目了然。</span><img alt="" src="http://www.httechs.com/uploadImg/2014-09-11_16_33_13.gif" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">8、简约而不简单，在总结同行的问题上，我们将一些晦涩难懂的功能变得通俗易懂，只要会握鼠标，就会操作软件。</span></p><p><br/></p>', '1504022400', 0, 0),
(27, '内部邮件组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><strong style="padding: 0px; margin: 0px;">内部邮件组件主要实现了以下功能：</strong></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><strong style="padding: 0px; margin: 0px;">&nbsp; &nbsp; &nbsp; &nbsp; 1.</strong>对内网邮件的管理。通过内网服务器，可以方便的接收、发送及管理内网邮件，其中内网邮件可以当作公司或企业的内部通知；</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong style="padding: 0px; margin: 0px;">2. &nbsp;</strong>对外网邮件的管理。通<span style="padding: 0px; margin: 0px; font-size: 9px;">过服务器</span>接入外网，可以实现本地收取和发送外网邮件，同时可对外网邮件进行基本的管理；</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp; &nbsp; &nbsp; &nbsp;<strong style="padding: 0px; margin: 0px;">&nbsp; 3.</strong>可以通过添加外网邮箱帐号信息进行外网邮件的管理。</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_58_39.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">（图：内部邮件组件）</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_55_51.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">(图2：发送内部邮件截图）</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_56_15.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">(图3：下载邮件附件)</p><p><br/></p>', '1504022400', 0, 0),
(28, '网络书签管理组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">网络书签组件主要完成了以下功能：</strong></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1.收藏常用的网络资源地址;</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;2.对网络资源进行分门别类管理</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3.实现内部用户间的网络资源分享</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="/ueditor/php/upload/image/20170830/1504053816126040.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp;(图1：网络书签视图模式)</span></p><p><br/></p>', '1504022400', 0, 0);
INSERT INTO `ht_article` (`id`, `title`, `cat_id`, `content`, `public_date`, `hits`, `list_order`) VALUES
(29, '工作管理组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">系统主要完成了以下功能：</strong></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">&nbsp; &nbsp; &nbsp; 1.&nbsp;&nbsp;</strong>可以快速为人员安排工作，跟踪工作进度，实现工作进度的便捷化管理。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; &nbsp; &nbsp;<strong style="padding: 0px; margin: 0px;">&nbsp;2.&nbsp;</strong>对工作的分配管理，实现对新建工作分配人员功能；对工作进度的管理，用户可以方便的修改用户的工作状态，从而达到对工作进度的人性化和科学化管理。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="/ueditor/php/upload/image/20170830/1504053868792817.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (图：工作管理组件)</span></p><p><br/></p>', '1504022400', 0, 0),
(30, '群管理组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">系统主要完成了以下功能：&nbsp;</strong></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">&nbsp; &nbsp; &nbsp; &nbsp;a.</strong>群管理（只有管理员权限才可访问）：</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp; i. 群申请管理：管理所有用户的群申请，实现群审批功能。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; ii. 当前群管理：管理当前所有的群，管理员对所有群的增删改功能。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; iii. 创建新群功能：可为任意用户创建一个新群，创建成功后将发送邮件通知 &nbsp; 用户。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; iv.创建群类别功能：可根据需要添加新的群类别。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; &nbsp;v.创建模板信功能。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">&nbsp;b.</strong>我的群（所有用户均可访问）：</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; &nbsp;i&nbsp;.创建新群功能：用户创建新群，需提交到管理员进行审核。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; &nbsp;ii.&nbsp;查看群信息功能：可从我的群列表中任选一个群查看该群的详细信息。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; iii.我的群功能：查看所有与当前用户相关联的群。并且显示用户的角色类型（群主或普通成员）。若用户为该群群主，则拥有对该群成员的管理权限。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; iv.修改群信息功能：只有群主用户才可对该群的信息进行修改。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; &nbsp;v.退群功能：普通用户可对已加入的群进行退群操作。群主用户可对已创建的群进行撤销操作。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 21pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp; &nbsp; &nbsp;vi.群浏览功能：用户可选择按类别或关键字查找的方式进行群浏览操作。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 42pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">同时，用户可选择任意群进行加入群操作。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_54_04.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">(图1：群浏览功能)&nbsp;</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-15_15_01_51.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">(图2：查看我的群功能)</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-15_15_02_05.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">(图3：群管理功能)</span></p><p><br/></p>', '1504022400', 0, 0),
(31, '内容发布组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;网站内容发布组件，是将网页上的某些需要经常变动的信息，类似新闻、新产品发布和业界动态等更新信息集中管理，并通过信息的某些共性进行分类，最后系统化、标准化发布到网站上的一种网站应用程序。并且内容页中支持插入图片方式和网页代码。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;</span><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_53_36.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">(图：内容发布组件)</p><p><br/></p>', '1504022400', 0, 0),
(32, '企业内部论坛组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">论坛组件主要实现以下功能：</strong></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 1.版块权限管理功能，用户只能在拥有相应版块权限的情况下才可进行发帖操作；</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;2.用户可对自己的发帖和回帖进行重新编辑，删除等功能；</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;3.用户通过图像管理功能，可方便地实现图像的切换及上传等操作；</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;4.论坛管理员可对论坛版块的优先级进行重新调整，管理用户发帖（如：删帖、置顶及取消置顶等）；</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5.论坛组件具有完善的发帖管理机制，实现论坛的科学管理。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_53_02.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px;">（图1：浏览帖子）</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_52_32.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">(图2：发表帖子)</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_51_09.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">(图3：论坛版块管理)</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_52_09.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">(图4：角色发帖权限管理)</p><p><br/></p>', '1504022400', 0, 0),
(33, '个人文件柜组件', 7, '<p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">&nbsp;文件柜组件主要实现以下功能：</strong></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1. 用户对个人文件的具有基本管理功能；</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2.文件柜在服务器中存储空间由管理员统一分配，实现对个人文件柜容量的管理；</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3.用户上传文件数量不受限制，并可批量上传文件；</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;4.用户可对已上传文件的重命名、删除、查询及分享等操作；实现了对文件柜中文件及文件夹的基本管理功能。</span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_21_03.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/></span></p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">（图1：列表模式）</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><img alt="" src="http://www.httechs.com/uploadImg/2014-09-12_17_21_29.jpg" style="padding: 0px; margin: 0px; border: 0px; vertical-align: middle; max-width: 90%; width: auto; height: auto;"/>&nbsp; &nbsp;</p><p style="padding: 10px 0px 8px; margin-top: 0px; margin-bottom: 0px; margin-left: 18pt; list-style: none; text-align: center; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">（图2：视图模式）</p><p><br/></p>', '1504022400', 0, 0),
(34, '企业信息化', 9, '<p><span style="color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; background-color: rgb(255, 255, 255);">&nbsp;作为新一代企业门户及协同工作平台，能快速创建企业门户、协同办公系统、知识管理系统、社区网站、业务分析系统等。 有利于企业建立高效率的协作执行体系，提高企业管理效率和水平，有效降低成本，增强核心竞争力。</span></p>', '1504022400', 0, 0),
(35, '电子政务', 10, '<p><span style="color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; background-color: rgb(255, 255, 255);">&nbsp;整合政府信息资源，建设政府“网站群”，从电子政务全局考虑，实现有序互联，有效共享。通过整合协同政府后台各部门和系统，提供及时高效的在线服务。全方位提高用户体验，提供个性化门户，提高门户的亲民性和便民性。根据需要可以灵活裁剪，灵活扩充，适用于各级政府。</span></p>', '1504022400', 0, 0),
(36, '数字化校园', 11, '<p><span style="color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; background-color: rgb(255, 255, 255);">&nbsp;建立合理高效的教学管理门户与教学内容管理系统。学生之间的协作与互动打破了传统上的时间与空间的束缚。可以开展e课堂，在线视频教学。通过共享协作和交互功能激发学生自主、协作学习的兴趣，促进校方、教师、学生之间的互动交流协作。</span></p>', '1504022400', 0, 0),
(37, '李克强指令：国家信息化先破政府信息“孤岛”', 13, '<p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">12月7日的国务院常务会议通过了《“十三五”国家信息化规划》。信息化既然是国家规划，政府当作表率。李克强总理的表态和要求，便是给各部门作出的表率。</p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">　　总理明确说，信息孤岛要坚决打通，起码政府系统不应再有。会议确定的规划重点，首先便是打破信息壁垒和“孤岛”，构建统一高效、互联互通、安全可靠的国家数据资源体系，打通各部门信息系统，推动信息跨部门跨层级共享共用；实施“互联网+政务服务”等信息惠民工程，加快推进公共数据资源向社会开放。</p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">　　所谓信息化，不仅在于“有”，更在于“通”。尤其是在互联网时代，信息必须实现有效流通和共享，才谈得上“化”。关于这一点，本届政府的思路和举措始终瞄准全球科技革命新浪潮和政府施政新理念。李克强总理力倡“互联网+”，写进了政府工作报告。其中，在政府端，结合简政放权放管结合优化服务的“自我革命”，就是围绕“互联网+政务服务”着重发力的。</p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">　　客观说，我国信息化已有相当基础。一个显著标志是，各部门建设起为数众多的各类信息系统。然而进一步信息化的梗阻恰恰也在于此。这些系统之间缺少联接和沟通，往往“各自为政”，形成了一个个信息“孤岛”。</p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">正因为此，总理今年以来已多次要求治理政府信息孤岛这一弊端。他指出，目前我国信息数据资源80%以上掌握在各级政府部门手里，“深藏闺中”是极大浪费；一些地方和部门的信息化建设各自为政，形成信息孤岛和数据烟囱，给企业群众办事创业造成很大不便。不久前召开的深化“放管服”改革座谈会上，李克强再次“点题”，各部门的各类信息系统，甚至一个部门内的各个系统，打不通的话，“是多大的资源浪费？”“老百姓办事怎么能方便？”</p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">　　政府信息孤岛的背后，既有部门利益、揽权的算盘，也有不愿作为、懒政的拖延。事实上，另一个被总理屡屡提及、老百姓也抱怨不止的问题——“奇葩证明”，就与信息孤岛直接相关。因为部门信息不共享，公民的户籍、就业、生育、婚姻等基本信息处于分散的碎片化状态。这导致一些明显不合理的证明，由于不同部门难以掌握相关信息，彼此之间仍要求通过纸质的证明，才能验证事实真伪。这样一来，群众便只能在分散的部门之间“跑断腿”、“寒透心”。</p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);">　　“孤岛”隔绝的不仅是信息本身，它甚至在某种程度上疏离了政府的服务和这种服务首要的对象——老百姓。简政放权，首先是为了百姓的福祉；推进信息化，也首先应体现在群众能享受信息化的便利。国家信息化必先破政府信息“孤岛”。总理在常务会上给“十三五”国家信息化规划定的重点，可谓切中肯綮。</p><p><br/></p>', '1504022400', 0, 0),
(38, '汉腾集成协作平台', 13, '<p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">创业板市场持续火爆，成长性企业成为资本关注的焦点。但对成长性组织来说，团队规模、业务规模、IT应用快速增加，团队管理、业务管理和IT管理复杂程度提高都对企业管理提出高挑战。如何突破管理效益、管理成本、信息化的壁垒，如何打破组织边界、降低成本、融合信息化，成为持续成长的关键因素。汉腾集成协作平台成为解决以上问题的不二之选。</span></p><p style="text-align:center;padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-10-15_09_53_33.jpg" style="padding: 0px; margin: 0px; border: 0px; max-width: 100%; vertical-align: middle; width: auto; height: auto;"/></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　　汉腾集成协作平台融合了各种IT技术，包括门户技术、权限技术、开放技术、整合技术、工作流技术、消息引擎技术、知识管理技术、表单技术、多核多线程技术、易用性技术等各种领先技术，具有协同管理平台的技术优势。汉腾集成协作平台在易用性、全兼容、SOA、分层扩展、四维整合、多元工作流、集团化权限、门户3.0、WEB2.0、多核多进程多部署的十大核心技术领先同行。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">　　1、建规矩 摆方圆</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　　汉腾集成协作平台通过支持数据表定义、表单定义、流程定义等多种手段，以图形化、透明化、可视化而完成制度的控制执行，保证制度执行的准确、高效、可跟踪和追溯，从而保证了制度的有效执行。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">　　2、积跬步 至千里</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　　随着组织的发展，单位或部门内部的制度、流程、工作成果、经验知识、工作方法、工作技巧等都是非常宝贵的资产。汉腾集成协作平台建立了功能完备的知识管理模块，用户可以方便的设置多种知识库，并支持权限的分发与控制，以及对知识的点评、分类检索等，以有效的管理这些宝贵的无形资产。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　<strong style="padding: 0px; margin: 0px;">　3、文化兴企 理念留人</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　　文化是支撑企业的脊梁，理念是推动发展的动力。随着组织不断的发展壮大，组织成员人数的不断增加，企业文化的建设尤其必要。汉腾集成协作平台是网络办公环境下企业文化建设的的最好工具。协同办公系统是全员应用的系统，可以中设立专门的栏目发布企业的文化理念体系、或经常性的在论坛中探讨企业文化，或将企业的简报定期发布等等，这一切都形成一种无处不在、无时不在的文化氛围。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　<strong style="padding: 0px; margin: 0px;">　4、实时监控 尽在掌握</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　&nbsp;&nbsp;<span style="padding: 0px; margin: 0px; color: rgb(255, 0, 0);">&nbsp;</span>汉腾集成协作平台充分利用了互联网无处不在的特性，组织成员可以在任何时间、任何地点方便的登陆系统查阅各种资料，查阅或办理各种协同工作、流程审批情况，可以方便的查阅计划日程、工作任务的下达完成情况。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　<strong style="padding: 0px; margin: 0px;">　5、打破常规 自由办公</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　　汉腾集成协作平台可以打破组织边界与地理位置边界，组织成员可以通过多种通讯形式进行沟通，如邮件、短信、网络会议、网络电话等。同时可以完成组织成员之间的文档等资料的互传、共享、分享；任务的传递、信息的沟通更加快捷、高效。通过协同办公，可以使组织建立起跨地域、跨部门、跨专业的协作环境，使组织内的 成员不用见面就完成高效、透明、有序合作。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　<strong style="padding: 0px; margin: 0px;">　6、分工明确 有条不紊</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　　计划管理可以让组织内每一个成员对自己的详细的工作进行详细安排，工作任务可以个人安排，可以领导安排，也可以是同级协助。而领导可以方便的查看下属的工作安排和个人相关情况，从而对部门内工作进行统筹安排，让组织内每个成员都可以有条理的处理自己的所有工作，不必在为工作的撞车而烦恼。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">　　7、合纵连横 一统天下</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　　目前多数组织已经有不止一套应用系统，而且随着组织的发展在壮大应用可能越来越多，同时信息孤岛也越来越多。汉腾提供多种方式进行信息系统的集成，从界面、功能、数据都进行了有效的整合，采用了SSO、PORTAL等技术，为企业应用建立了统一的门户，为企业建立了统一的形象。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 14px;">　　汉腾软件不仅帮助众多成长型企业解决管理难题，又自主研发的汉腾集成协作平台融合各种管理思想，包括问题管理、过程管理、计划管理、时间管理、知识管理、权变管理、人性化管理、文化管理、科学管理、沟通管理等各种管理学派中关于组织中个体、团队、部门的理论成果，构建组织快速成长的所需要的管理工具平台，帮助企业打破管理边界、整合IT孤岛，从而形成有序、实时、规范、开放、高效率、低成本、柔性、有凝聚力的管理环境和一体化的技术支撑环境，帮助企业在实现管理思想的有效落地，助力企业继续高速成长。</span></p><p><br/></p>', '1504022400', 0, 0);
INSERT INTO `ht_article` (`id`, `title`, `cat_id`, `content`, `public_date`, `hits`, `list_order`) VALUES
(39, '信息化新挑战 国产软件已成主角', 13, '<p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">不论大型企业建设与运维信息系统的模式如何，根本上是为企业的运营管理和业务服务。由于市场等因素不断变化，企业也随之改变。所以，企业的信息化是一个长期的、动态的过程，期间也会遇到众多的问题与挑战，解决不当，将会极大影响企业的发展。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">海比研究调研发现，高达90%的CIO表示，目前大型企业的信息化遇到了最为棘手的问题，这也是他们从事信息化以来最难以应付的挑战。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">调查发现，新挑战主要可归结为三类：</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">新技术、新模式带来的新应用需求;</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">信息系统的建设、维护所需的成本;</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">信息系统面临的安全新挑战。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">大型企业信息化挑战 (N = 127)</span></p><p><span style="padding: 0px; margin: 0px; font-size: 12px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-10-16_10_19_35.jpg" style="padding: 0px; margin: 0px; border: 0px; max-width: 100%; vertical-align: middle; width: auto; height: auto;"/></span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;"><span style="padding: 0px; margin: 0px; border-width: 0px; border-style: initial; border-color: initial; outline-style: none; outline-width: 0px; background: none 0% 0% repeat scroll transparent; vertical-align: baseline; font-weight: 700;">1)技术</span></span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">新的信息技术已经成为推动社会经济发展的重要引擎，正重构整个社会商业生态。在云计算、移动互联网、电子商务、大数据、物联网、企业社交、3D打印等新一轮的信息技术的影响下，大型企业的信息化面临着巨大的挑战。在调研中，33%的企业信息化负责人认为满足新技术、新模式带来的新应用需求，是大型企业信息化的重要挑战。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;"><span style="padding: 0px; margin: 0px; border-width: 0px; border-style: initial; border-color: initial; outline-style: none; outline-width: 0px; background: none 0% 0% repeat scroll transparent; vertical-align: baseline; font-weight: 700;">2)成本</span></span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">调查发现，38%的大型企业信息化负责人认为，成本是当前大型企业信息化建设的另一大挑战。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">近年来，由于人力、物料、物流等环节的成本上升，在信息化的投资方面更为注重回报率。这主要表现在，大型企业在信息化投资时更加注重性价比高的信息化产品。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">如何应对大型企业信息系统中大量应用且成本居高的国外软件产品和解决方案，成为日益困扰他们的严重挑战。在很多大型企业中，昂贵的国外产品正逐渐成为大型企业信息化成本支出的大户，也是很难降下来的“钉子户”。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;"><span style="padding: 0px; margin: 0px; border-width: 0px; border-style: initial; border-color: initial; outline-style: none; outline-width: 0px; background: none 0% 0% repeat scroll transparent; vertical-align: baseline; font-weight: 700;">3)安全</span></span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">调查发现，41%的大型企业信息化负责人认为，安全是当前信息化最大挑战，排在所有挑战的首位。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">事实上，86%的大型企业负责人在信息化建设时会考虑到安全问题。尤其是棱镜门事件后，安全已经成为企业在信息化建设时考虑的关键因素之一。信息系统的安全主要来自于两个方面：</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">原有应用系统面临的安全问题;</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">企业运营管理与业务升级面临的安全问题。</span></p><p><span style="padding: 0px; margin: 0px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-10-16_10_19_56.jpg" style="padding: 0px; margin: 0px; border: 0px; max-width: 100%; vertical-align: middle; width: auto; height: auto;"/></span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-align: center; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">贵公司信息化时是否考虑安全因素? (N = 127)</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;"><span style="padding: 0px; margin: 0px; border-width: 0px; border-style: initial; border-color: initial; outline-style: none; outline-width: 0px; background: none 0% 0% repeat scroll transparent; vertical-align: baseline; font-weight: 700;">原有应用系统面临的安全问题</span></span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">应用系统直接运行与管理着企业的数据，其泄漏可能会给企业运行、乃至整个国家的产业安全带来巨大的危害。大型企业原有应用系统面临的安全问题主要有五个方面：</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">国外产品可能存在安全隐患，预留了“后门”;</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">通过对系统进行升级，引入新的“后门”;</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">在线升级的方式，容易受到来自网络攻击的危险;</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">停止升级服务，信息系统的漏洞得不到补救，随着黑客技术手段的升级，被入侵的风险增大;</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">二次开发，应用管理软件往往都有二次开发平台，在二次开发时也可能预留后门程序。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">原有应用系统面临的主要安全隐患</span></p><p><span style="padding: 0px; margin: 0px;"><img alt="" src="http://www.httechs.com/uploadImg/2014-10-16_10_20_13.jpg" style="padding: 0px; margin: 0px; border: 0px; max-width: 100%; vertical-align: middle; width: auto; height: auto;"/></span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;"><span style="padding: 0px; margin: 0px; border-width: 0px; border-style: initial; border-color: initial; outline-style: none; outline-width: 0px; background: none 0% 0% repeat scroll transparent; vertical-align: baseline; font-weight: 700;">企业运营管理与业务升级面临的安全问题</span></span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">一方面，由网络技术带来的服务变化，越来越多的大型企业要求信息系统具备开放性，使企业的信息可以在互联网上进行即时交流，开放性使得系统随时随地存在被恶意攻击的可能性，增大了保护企业信息安全的难度;另一方面，信息系统的升级，可能会带来新的模块，如果使用国外软件进行系统扩充，则存在引入新后门的安全风险。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">数据表明，技术、成本、安全是当前大型企业信息化建设的三大挑战。这些挑战第一次在企业面前集中爆发，但又必须满足这些需求，大型企业信息化负责人做了各种努力和尝试，取得了很多成果。</span></p><p style="padding: 5px 0px; margin-top: 15px; margin-bottom: 15px; list-style: none; white-space: normal; border-width: 0px; border-style: initial; border-color: initial; text-indent: 2em; outline-style: none; outline-width: 0px; font-stretch: normal; font-size: 14px; line-height: 26px; font-family: 微软雅黑; background: rgb(255, 255, 255); color: rgb(34, 34, 34); vertical-align: baseline;"><span style="padding: 0px; margin: 0px;">在整理大型企业解决信息化挑战的案例中发现：在新建设的信息系统中，尽可能地使用融合了新技术的国产化软件和解决方案;对于已应用的一些国外软件产品和解决方案进行国产化替代，这是同时解决三大挑战的有效方法。在海比研究选取的20多个应对挑战的经典案例中，信息系统建设的国产化是战胜挑战的法宝。</span></p><p><br/></p>', '1504022400', 0, 0),
(40, '李克强：整合各类政府信息平台避免重复建设和数据打架', 13, '<p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">“大数据发展”有了指南 消除信息孤岛</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　国务院常务会议通过促进大数据发展行动纲要，推动政府信息系统和公共数据互联共享</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;"><strong style="padding: 0px; margin: 0px;">整合政府信息平台 避免数据“打架”</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　会议认为，开发应用好大数据这一基础性战略资源，有利于推动大众创业、万众创新，改造升级传统产业，培育经济发展新引擎和国际竞争新优势。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　会议通过《关于促进大数据发展的行动纲要》，强调，一要推动政府信息系统和公共数据互联共享，消除信息孤岛，加快整合各类政府信息平台，避免重复建设和数据“打架”，增强政府公信力，促进社会信用体系建设。优先推动交通、医疗、就业、社保等民生领域政府数据向社会开放，在城市建设、社会救助、质量安全、社区服务等方面开展大数据应用示范，提高社会治理水平。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　二要顺应潮流引导支持大数据产业发展，以企业为主体、以市场为导向，加大政策支持，着力营造宽松公平环境，建立市场化应用机制，深化大数据在各行业创新应用，催生新业态、新模式，形成与需求紧密结合的大数据产品体系，使开放的大数据成为促进创业创新的新动力。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　三要强化信息安全保障，完善产业标准体系，依法依规打击数据滥用、侵犯隐私等行为。让各类主体公平分享大数据带来的技术、制度和创新红利。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　<strong style="padding: 0px; margin: 0px;">李克强：部门数据有时“打架”这怎么建现代政府呢？</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　自2014年3月将“大数据”首次写入《政府工作报告》以来，李克强总理在多个场合提及这一“热词”。8月19日常务会的议题之一，是讨论研究《关于促进大数据发展的行动纲要》。他强调，制定大数据行动纲要，要突出围绕“政府大数据建设”和“创造健康发展的大数据环境”这两项核心内容展开。总理的讲话重点凸现了三个“关键词”。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　<strong style="padding: 0px; margin: 0px;">【关键词1：共享】</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　<strong style="padding: 0px; margin: 0px;">“首先要实现所有部门的数据共享”</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　“目前，政府各部门已经建成了十几个数据平台，但问题是，这些平台相互不连通，只是一个一个的‘信息孤岛’。”李克强针对政府大数据现状说，“本来，信息的连接是最容易的，商业网站之间都是连接的，但我们各部门的数据就不行，我的网就是我的网，别的部门要用，就是进不来！”</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　他向有关部门明确要求：政府大数据建设，首先要实现所有部门的数据“共享”。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　推动政府信息互联互通，实现信息“共享”，是李克强就任总理以来一直盯着不放的问题。去年7月25日，他在山东浪潮集团考察调研期间，特意把相关部门负责人叫到身边“现场办公”，要求他们要以云计算、大数据理念，与信息技术平台有机对接，建立统一综合的信用信息平台，实现“大数据”共享。在今年6月17日的国务院常务会议上部署应用大数据时，李克强再次强调，要注重数据、信息之间的关联，进一步推动政府信息开放共享，消除信息的“盲区”、“孤岛”。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　而在8月19日的常务会议讨论研究《关于促进大数据发展的行动纲要》时，李克强重点强调：各部门的数据首先要实现“共享”。“什么叫共享？起码要有个连接，打开你的就能把其他的打开。共享过程中如果出现重叠，那就要进行衔接，实现数据整合。”他说。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　<strong style="padding: 0px; margin: 0px;">【关键词2：开放】</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　<strong style="padding: 0px; margin: 0px;">“信息不共享会阻碍社会信用体系建立”</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　对于政府大数据建设，李克强谈到的第二个关键词是“开放”。他说：“有一些引导社会经济发展的数据，不涉及国家秘密的，都应该向公众开放，以方便大家使用。”</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">李克强说，特别是一些与信用相关的数据平台，如果信息不共享、不开放，就会阻碍社会信用体系的建立。从另一方面讲，相关数据的开放，也会倒逼政府数据发布的真实性和规范性。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　“现在一些地方、部门发布的数据，有时候会相互‘打架’，<span id="stock_sh603883" style="padding: 0px; margin: 0px;"><a href="http://finance.sina.com.cn/realstock/company/sh603883/nc.shtml" target="_blank" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">老百姓</a></span><span id="quote_sh603883" style="padding: 0px; margin: 0px;">都搞不清楚究竟哪些数据是真实的。”李克强说，“这怎么提高社会治理能力，怎么建设现代政府呢？”他要求，政府大数据建设不能仅仅“划一个概念”：“首先要把政府大数据的建设事情办好，给社会一个好的示范。”</span></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　<strong style="padding: 0px; margin: 0px;">【关键词3：安全】</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　<strong style="padding: 0px; margin: 0px;">“政府既要扶持又要规范，保障信息安全”</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　在8月19日的常务会议上，李克强强调的第三个关键词是“安全”。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);"><span style="padding: 0px; margin: 0px; font-size: 16px;">　　他说，互联网等行业已经在不断创新、发展大数据产业。“因此，政府既要‘扶持’，为大数据产业创造一个健康发展的环境，又要‘引导’、‘规范’，保障信息安全。”他说：“大数据产业的潮流不可阻挡，我们要顺势而为。” 据中国政府网</span></p><p><br/></p>', '1504022400', 0, 0);
INSERT INTO `ht_article` (`id`, `title`, `cat_id`, `content`, `public_date`, `hits`, `list_order`) VALUES
(41, '软件工程前沿开发技术', 16, '<p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">经过近四十年的发展，软件工程在支持软件系统工程化开发方面取得了令人瞩目的成绩，提出了大量的理论、方法、技术和工具，但是近年来的研究和实践表明软件危机依然存在，软件开发仍然存在成本高、质量得不到保证、进度和成本难以控制等方面的问题，许多软件项目被迫延期甚至取消。与此同时，随着网络技术（尤其是Internet技术）的不断发展，部署在网络基础上的软件系统的规模和复杂性越来越高，并表现出诸如持续性、自适应性、交互性、动态性、开放性、异构性等特点。因此，如何支持这类复杂系统的开发、缓解和消除现阶段的软件危机是当前软件工程面临的一项重要挑战。为了迎接上述挑战，近年来软件工程领域的一些学者提出了许多新的方法和技术，包括：敏捷软件开发（Agile Software Development）、极限编程（Extreme Programming，XP）、测试驱动的开发（Test-Driven Development，TDD）、面向Agent的软件开发（Agent-Oriented Development）、面向方面的编程（Aspect-Oriented Programming，AOP）、模型驱动体系结构（Model-Driven Architecture，MDA）等等。与传统的软件工程方法相比较，这些方法和技术为软件工程实践提供了新的思路，已在许多软件工程实践中取得了积极的效果。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><a href="http://www.itisedu.com/phrase/200604240937105.html" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">用例</a>、测试代码和功能代码，逐步完成整个软件模块的功能。这种循序渐进的做法可以防止疏漏，避免干扰其它工作。（4）测试驱动，要实现某个功能、编写某个类，程序员首先应编写相应的测试代码和设计相应的测试用例，然后在此基础上编写程序代码。（5）先写断言，在编写测试代码时，程序员应首先编写对功能代码进行判断的断言语句，然后再编写相应的辅助语句。（6）及时重构，程序员在编码和测试过程中应对那些结构不合理、重复的程序代码进行重构，以获得更好的软件结构，消除冗余代码。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">与传统的软件编码和测试方式相比较，测试驱动开发具有以下的一组优点。（1）编码完成后即完工，在程序代码编写完成并通过测试之后，意味着编码任务的完成。而在传统的方式中，由于编码完成之后需要进行单元测试，因而很难知道什么时候编码任务结束。（2）易于维护，软件系统与详尽的测试集一起发布，有助于将来对程序进行修改和扩展，并且在开发过程中及时对程序代码进行重构，提高了软件系统的可维护性。（3）质量保证，由于任何程序代码都经过了测试，因而有助于有效发现程序代码中的错误，提高软件系统的质量。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><a href="http://cppunit.sourceforge.net/cppunit-wiki" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">cppUnit</a>、<a href="http://www.csunit.org/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">csUnit</a>、<a href="http://cunit.sourceforge.net/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">CUnit</a>、<a href="http://dunit.sourceforge.net/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">DUnit</a>、<a href="http://www.dbunit.org/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">DBUnit</a>、<a href="http://www.junit.org/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">JUnit</a>、<a href="http://www.ndbunit.org/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">NDbUnit</a>、<a href="http://www.ounit.com/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">OUnit</a>、<a href="http://phpunit.sourceforge.net/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">PHPUnit</a>、<a href="http://pyunit.sourceforge.net/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">PyUnit</a>、<a href="http://www.nunit.org/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">NUnit</a>、<a href="http://www.vbunit.org/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">VBUnit</a>等等。本节介绍用Java进行测试驱动开发所使用的标准工具JUnit，下节将详细分析如何利用JUnit来支持测试驱动开发。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">JUnit是一个由 Erich Gamma 和 Kent Beck 二人共同开发的开源Java单元测试框架。JUnit框架提供了一组类来支持单元测试。通过继承重用这些类，程序员可以方便的编写测试程序代码，运行测试程序以发现程序代码中的故障。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">JUnit的主要类结构如图 1所示。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Test</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">这是一个接口，所有测试类（包括TestCase和TestSuite）必须实现该接口。Test提供了两个方法：<a href="http://blog.163.com/hcy/Application%20Data/Microsoft/Word/TDD/junit3.8.2/junit3.8.2/javadoc/junit/framework/Test.html#countTestCases()" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;"><span style="padding: 0px; margin: 0px; color: rgb(0, 0, 0);">countTestCases</span></a>方法用于计算一个测试将要运行的测试用例的数目；<a href="http://blog.163.com/hcy/Application%20Data/Microsoft/Word/TDD/junit3.8.2/junit3.8.2/javadoc/junit/framework/Test.html#run(junit.framework.TestResult)" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;"><span style="padding: 0px; margin: 0px; color: rgb(0, 0, 0);">run</span></a>方法用于运行一个测试并收集它的测试结果。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Assert</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">该类定义了软件测试时要用到的各种方法。例如assertEquals方法用于判断程序代码的运行结果是否等同于预期结果；assertNull和assertNotNull方法用于判断对象是否为空等等。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TestCase</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">TestCase类实现了Test接口并继承了Assert类，它是程序员在编写测试程序时必须扩展的类。通过继承，程序员可以方便的利用该类提供的方法对程序单元进行测试。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TestSuite</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">TestSuite类实现了Test接口并提供了诸多方法来支持测试，当程序员试图将多个测试集中在一起进行测试时必须扩展该类。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><a href="http://www.nar.fujitsulabs.com/people/frank.html" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">G. McCabe</a>&nbsp;和&nbsp;<a href="http://www-lp.doc.ic.ac.uk/~klc/" rel="nofollow" style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); text-decoration-line: none;">Keith L. Clark</a>提出的April</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">April（Agent Process Interaction Language）是由Francis G. McCabe和Keith L. Clark开发的一个基于对象的符号处理程序设计语言。它支持多Agent系统的开发，为Internet上软件Agent的开发和发布提供各种服务，是AprilAgent平台（April Agent Platform）的一个组成部分。实际上，将该语言归类于“基于对象技术”类别并不完全准确，因为April语言借鉴和包含了许多来自非面向对象语言的一些思想和机制，如Parlog、Erlang、PCN、CSP、Dijkstra的卫式命令、LISP、Prolog 等。April程序的基本执行单元是一个个的主动对象，它们可并发地执行。每个主动对象对应于一个进程，不同的主动对象之间可以基于FIPA ACL进行交互和通信。从语法上看，April有点类似于C和C++语言。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">4.4.3 Java Agent Language (JAL)</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">JAL（Java Agent Language）是一个基于Java、支持对面向Agent软件系统进行编程的程序设计语言，它是面向Agent集成开发环境JACK Intelligent Agent?的一个组成部分。JAL将Agent视为是一种特殊的对象，它具有特殊的内部结构，能够持续地运行并能对外部的刺激（如消息或者事件）作出自主的响应，从而展示某些理性和智能的行为。JAL将这类Agent称为智能Agent（Intelligent Agent）。JAL通过对Java这一面向对象程序设计语言进行扩展，从语法机制、语义内容和基础类库等几个方面为面向Agent软件系统的开发提供程序设计语言级的支持。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">n&nbsp; 基础类库</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">JAL在Java类库的基础上定义了一组新的、可重用的基类和接口。这些JAL基类和接口封装和实现了软件Agent的基本功能（如Agent的自主行为决策、消息的发送和接收、信念维护等等）。JAL主要引入了以下一组类一级的可重用软部件：Agent、Capability、OpenWorld和ClosedWorld、Event、和Plan。Agent类封装了智能软件Agent的基本功能。Capability类支持定义Agent的能力，Agent的能力描述了Agent对外展示的功能，如能够对外部的事件作出响应、进行处理。OpenWorld和ClosedWorld这两个类支持定义Agent的信念即Agent对世界（包括其自身）的理解和认识，封装了信念的自动维护功能。Event类支持定义系统中的各种事件包括内部事件和外部事件，在JAL中事件是促使Agent实施自主行为的一个主要因素。Plan类支持定义Agent的规划。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">软件开发人员可以通过继承、实现等手段来重用这些基类和接口，从而来开发与特定应用相关的Agent、事件、规划、能力等软部件。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">n&nbsp; 扩展语法</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">为了支持对面向Agent软件系统进行编程，JAL对标准的Java语法进行了多种形式的扩充。这种扩充主要表现为以下三个层次。&nbsp;</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">(1)&nbsp;&nbsp;&nbsp; 类定义层</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">提供了一组可重用的JAL类，它们封装了面向Agent软件系统的基本功能，如Plan、Event、Agent等。程序设计人员可以通过继承来重用这些类从而来定义新类。“plan MovementResponse extends Plan”语句描述了通过继承JAL提供的基类“Plan”来定义一个规划类“MovementResponse”。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">(2)&nbsp;&nbsp;&nbsp; 声明层</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">提供了一组以“#”符号开头的语句用于标识和定义构成软件Agent各个成分之间的关系。在图26所示的例子中，“#handles event RobotMoveEvent moveResponse;”就是一个声明层的语句，它说明规划类“MovementResponse”用于响应和处理“RobotMoveEvent”类型的事件“moveResponse”，进而描述了规划类“MovementResponse”和事件类“RobotMoveEvent”之间的关系。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">(3)&nbsp;&nbsp;&nbsp; 语句层</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">提供了一组以“@”符号开头的语句用于操作由JAL描述和定义的数据结构。图26中的“@send(moveResponse.from, moveResponse);”是一个语句层的语句，它表示向“moveResponse.from”Agent发送一个“moveResponse”消息。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">加粗的程序代码是JAL特有的语法元素，而正常非加粗文字则是普通的Java代码。因此，JAL程序实际上就是一个特殊的Java程序。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">n&nbsp; 扩展Java引擎</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">JACK集成开发环境提供了一个JAL编译器和一个核心运行引擎。JAL编译器负责将用JAL语言编写的程序代码编译为纯Java代码。然后由Java编译器编译生成可执行的Java中间码并在JACK核心运行引擎上运行。JACK核心运行引擎提供了JAL程序运行所需的底层基础设施和功能。由于Agent运行机制与对象运行机制有着本质性的差别，因此JACK核心运行引擎从以下方面扩展了Java运行引擎。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">(1)&nbsp;&nbsp;&nbsp; 多线程被直接集成到JACK运行内核中而不受程序员控制。</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">由JAL编程得到的软件Agent是一个并发的、具有多个线程，能够持续性运行的软件实体。Agent的多线程特征是由JACK的运行引擎提供的，JAL不允许程序员利用Java语言的多线程机制来显式地编写多线程程序。因此，在JAL中多线程机制对于软件开发人员而言是透明的。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">(2)&nbsp;&nbsp;&nbsp; 按照Agent执行模型来实施Agent的行为。</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">在JACK中，Agent是一个具有BDI和反应式体系结构的计算实体，因此它按照BDI理论和反应式规则来实施行为。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">(3)&nbsp;&nbsp;&nbsp; 引入了一个新的数据结构，称为逻辑成员。</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">不同于一般的成员，逻辑成员在没有被赋值之前，它的值是不可知的。一旦逻辑成员被赋值，那么它的值将不会发生改变。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">(4)&nbsp;&nbsp;&nbsp; 使用逻辑成员来表示Agent信念集的查询结果</strong>。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">Agent的信念集定义了Agent的一组信念。软件开发人员可以使用逻辑成员这一数据结构来统一地存储Agent信念的查询结果。一旦查询成功，逻辑成员就包含查询的结果值。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;"><a rel="nofollow" style="padding: 0px; margin: 0px;">1. Agent</a>的组成</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">JAL将Agent视为是一个具有BDI认知结构、能够对事件作出反应式处理的智能行为实体。因此，JAL中的Agent混合了BDI和反应式两种体系结构。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">根据Agent的BDI和反应式体系结构，JALAgent具有一组数据来表示当前的世界状态（即信念）；能感知系统中发生的事件并拥有一组希望实现的目标（即期望）；为了实现其目标以及对系统事件作出响应和处理，它还应具有一组规划，并能根据其信念、感知到的事件以及期望选择合适的规划来执行（即意图）。因此，JAL中的Agent既能展示某些目标制导的自主行为，也能表现出事件驱动的反应式行为。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">当一个软件Agent被实例化生成之后，它将处于等待状态直至它产生新的期望或者感知到系统中发生的事件。在此情况下，软件Agent将确定该采用什么样的动作来实现其期望或者对事件作出响应。如果它根据其信念发现期望已经实现或者事件已经得到了处理，那么它将不会采取任何行动。否则，它将从其规划集合中寻找适合于实现其期望或者对事件作出响应和处理的规划来执行。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">为了实现具有上述行为特征的软件系统，一个JAL程序一般包含以下一组功能性的单元（见图27）：</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">n&nbsp; Beliefset</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">一个Agent拥有一组信念以描述Agent对世界的理解和认识。JAL采用一种特殊的数据结构（即基于元组的关系模型）来表示和定义Agent的信念。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">n&nbsp; Event</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">一个Agent能够处理一个或者多个系统事件，而一个系统事件可以为一个或者多个Agent所处理。Agent对事件的处理既可以是反应式的，即根据某种规则立即执行相应的规划，也可以是审慎式的，即引发Agent产生某些期望从而导致Agent实施某些行为。JAL将事件定义为一种特殊的对象。Agent可以通过显式地发送事件来和其它Agent进行交互和通信。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">n&nbsp; Plan</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">一个Agent拥有一组规划用于响应系统事件、实现Agent的目标。每一个规划定义了一组动作序列。规划中的动作既可以用Java语句来表示（如用“println”语句打印一个结果）、也可以用JAL提供的语句来表示（如用“@send”语句发送一个消息）。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">n&nbsp; Capability</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">能力封装了信念、事件、规划等功能性单元，它描述了Agent能够访问和存取的信念集、能够感知和处理的事件以及对事件作出响应和处理、实现Agent目标所需的规划。因此，能力单元封装了Agent的功能。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;">n&nbsp; Agent</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">一个Agent实际上就是一个特殊的对象。它拥有一组能力、包含一组信念集关系Beliefset用于存储其信息、拥有一组规划、能够对一组事件进行响应和处理。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><strong style="padding: 0px; margin: 0px;"><a rel="nofollow" style="padding: 0px; margin: 0px;">2.&nbsp;</a>程序的执行</strong></span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">由JACK集成开发环境所提供的JAL编辑器将把程序设计人员所编辑的源代码保存为具有不同后缀的源程序文件（见表7）。一般地，一个源文件对应于一个JAL类。</span></p><p style="padding: 8px 0px; margin-top: 0px; margin-bottom: 0px; list-style: none; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: center;"><span style="padding: 0px; margin: 0px; font-size: 14px;"><a rel="nofollow" style="padding: 0px; margin: 0px;">&nbsp; &nbsp; &nbsp;表</a>7.JAL源程序的文件组织，其中“x”表示文件名&nbsp;</span></p><table cellpadding="0" cellspacing="0"><tbody style="padding: 0px; margin: 0px;"><tr style="padding: 0px; margin: 0px;" class="firstRow"><td valign="top" width="108" style="padding: 0px; margin: 0px;"><p style="padding-top: 8px; padding-bottom: 8px; list-style: none;">&nbsp;</p><p style="padding-top: 8px; padding-bottom: 8px; list-style: none; text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">文件后缀</span></p></td><td valign="top" width="169" style="padding: 0px; margin: 0px;"><p style="padding-top: 8px; padding-bottom: 8px; list-style: none;">&nbsp;</p><p style="padding-top: 8px; padding-bottom: 8px; list-style: none; text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">说明</span></p></td><td valign="top" width="251" style="padding: 0px; margin: 0px;"><p style="padding-top: 8px; padding-bottom: 8px; list-style: none;">&nbsp;</p><p style="padding-top: 8px; padding-bottom: 8px; list-style: none; text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">经JAL编译器的编译生成</span></p></td></tr><tr style="padding: 0px; margin: 0px;"><td valign="top" width="108" style="padding: 0px; margin: 0px;"><p style="padding-top: 8px; padding-bottom: 8px; list-style: none;">&nbsp;</p><p style="padding-top: 8px; padding-bottom: 8px; list-style: none; text-indent: 2em;"><span style="padding: 0px; margin: 0px; font-size: 14px;">x.event</span></p></td><td valign="top" width="1