INSERT INTO `ht_recruit` (`id`, `position`, `number`, `working_hours`, `address`, `responsibility`, `requirement`, `treatment`, `public_date`, `hits`) VALUES
(2, '互联网软件工程师', 2, '', '合肥火车西站望江西路与翡翠路交叉口福斯中心1604-1605室', '<p style="line-height: normal;">1、技术人员职位，在上级的领导和监督下定期完成量化的工作要求；<br style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/>2、能独立处理和解决所负责的任务；<br style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/>3、根据开发进度和任务分配，完成相应模块软件的设计、开发、编程任务；<br style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/>4、进行程序单元、功能的测试，查出软件存在的缺陷并保证其质量；<br style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/>5、进行编制项目文档和质量记录的工作；<br style="padding: 0px; margin: 0px; color: rgb(66, 66, 66); font-family: Arial, Helvetica, sans-serif, SimSun; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);"/>6、维护软件使之保持可用性和稳定性。</p>', '<p style="line-height: normal;"><span style="color: rgb(0, 0, 0);">1、计算机及相关专业毕业，大学本科以上学历；<br style="padding: 0px; margin: 0px;"/>2、精通ASP.NET、C#、HTML/XML、JavaScript、CSS、AJAX、ＪＱuery等技术;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br style="padding: 0px; margin: 0px;"/>3、三年以上ASP.NET、C#开发经验；熟悉.NET框架，熟悉.NET 控件的编写；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br style="padding: 0px; margin: 0px;"/>4、熟悉Web Service、NHibernate、了解设计模式等技术；<br style="padding: 0px; margin: 0px;"/>5、熟悉SQL Server数据库并对数据库的设计与实施有一定经验；&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br style="padding: 0px; margin: 0px;"/>&nbsp;6、具备良好的编程习惯和良好的英文读写能力。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br style="padding: 0px; margin: 0px;"/>&nbsp;7、做事严谨踏实，责任心强，条理清楚，善于学习总结，有良好的团队合作精神和沟通协调能力</span></p>', '', '1503849600', 0),
(8, '销售代表', 2, '', '合肥市高新技术开发区天达路2号安徽大学科技园', '<p style="line-height: normal;">1. 负责指定区域、行业内目标客户开发，伙伴拓展、维护；</p><p style="line-height: normal;">2.负责公司管理类软件产品销售工作；</p><p style="line-height: normal;">3.执行公司的销售策略，完成销售目标</p>', '<p>1．具备高度开拓和敬业精神、积极进取，敢于面对困难与挑战，能够在压力下工作；</p><p>2.&nbsp; 善于沟通，语言表达能力强，以客户为导向，能挖掘客户需求，有较强服务意识。</p><p>3.&nbsp; 有良好的团队合作协调能力，能经常出差；</p><p>4.&nbsp; 有企业管理软件行业销售经验者优先 ；</p>', '<p style="line-height: 1.75em;">双休,每周工作不超过40小时,法定节假日与国家标准相同;公司提供优雅的工作环境、优厚的薪资待遇（工资+提成+奖金）和完善的社会保障。</p>', '1504022400', 1);

